<?php

class LogsViewer {
    private $paths = [
        './storage/logs',
        './storage/logs2',
    ];
    private $logs = [];
    private $file;

    public function __construct() {
    }

    public function getLogs() {
        $op = [];
        foreach ($this->paths as $path) {
            $f = scandir($path);
            rsort($f);
            $op2 = array_map(function ($a) use ($path) {
                return $path . '/' . $a;
            }, $f);
            $op = array_merge($op, $op2);
        }
        return array_values(array_filter($op, function ($a) {
            $b = substr($a, strlen($a) - 4, strlen($a));

            return $b == '.log';
        }));
    }

    public function render() {

        $this->logs = $this->getLogs();
        if (!count($this->logs)) {
            echo 'no logs files found';
            die;
        }

        $selectedFile = isset($_GET['file']) && $_GET['file'] ? $_GET['file'] : $this->logs[0];
        $this->file = file($selectedFile);

        $logs = [];
        foreach ($this->file as $line) {
            if (strpos($line, '[202') !== false) {
                $logs[] = $line;
            }
            else {
                $logs[count($logs) - 1] .= "\n" . $line;
            }
        }

        $logs = array_reverse($logs);
        $total = count($logs);
        $logsHtml = '';
        foreach ($logs as $k => $l) {
            $lineNo = $total - $k;
            $indx = $k + 1;
            $line = str_replace("\n\n", "\n", $l);
            $expanded = !$k ? 'ex' : '';
            $title = substr($line, 21, 100);
            //            $title = substr($line, 21, strpos($line, "\n"));
            $er = substr($title, 0, strpos($title, ':'));
            $title = substr($title, strpos($title, ':') + 2);
            $er = strtolower(substr($er, strpos($er, '.') + 1));

            $logsHtml .= "
<div class='log $expanded' indx='$indx'>
    <div class='log-t'>
        <span class='ln'>
            line: $lineNo
        </span>
        <span class='log-t-buttons'>
            <button class='np'>newer</button>
            <button class='nn'>older</button>
        </span>
        <span class='ie $er'>
        </span>
        <span>
                $title
        </span>
    </div>
    <div class='log-c' style=''>
    <pre>$line</pre>
</div>
</div>
            ";
        }

        $selectHtml = "";
        $selectHtml .= "
                <option value=''>latest</option>
            ";
        foreach ($this->logs as $log) {
            $selectHtml .= "
                <option value='$log'>$log</option>
            ";
        }

        return "<html>
<link rel='icon' type='image/x-icon' href='data:image/png;base64,'>
<title>
$selectedFile
</title>
<style>
    body{
        margin: 0;
        padding: 0;
        font-family: Arial,Helvetica Neue,Helvetica,sans-serif;

    }
    .logs{

    }
.ie{
    height: 10px;
    width: 10px;
    display: inline-block;
    border-radius: 50px;
    margin-right: 0px;
}
.ie.error{
    background: crimson;
}
.ie.info{
    background: dodgerblue;
}
.ie.warning{
    background: orange;
}
    .logs .log .log-t{
        position: sticky;
        top: 0;
        background: #ddd;
//        bottom: 0;
        padding: 3px 20px;
    }
    .logs .log .log-t .ln{
        min-width: 200px;
        display: inline-block;
    }
    .logs .log .log-t > span{
            vertical-align: middle;
            font-size: 14px;
    }
    .logs .log .log-c{
        padding: 0px;
        max-height: 40px;
        overflow: hidden;
    }
    .logs .log.ex .log-c{
        overflow: auto;
        max-height: initial;
    }
    .logs .log .log-c pre {
        padding: 5px 20px;
        overflow: scroll;

    }
    .log-t-buttons{
    margin-right: 20px;
    }
    .log-t-buttons button{

    }
    .fn{
        font-size: 13px;
    }
    .top-right{
    position: fixed;
        top: 3px;
        display: none;
        z-index: 2;
        right: 8px;
    }
</style>
<script
  src='https://code.jquery.com/jquery-3.6.4.min.js'
  integrity='sha256-oP6HI9z1XaZNBrJURtCoUT5SUnxFr8s3BzRl+cbzUq8='
  crossorigin='anonymous'></script>
<script>
  $(() => {
       $('.f').change(function(a){
           let s=  $(this).val();
           console.log(s);
           location.search = '?file=' + encodeURIComponent(s);
       })
       $('.f').val('$selectedFile');
       $('.top-right').show();
       $('.log .log-c').click(function(){
//           console.log($(this).hasClass('ex'));
           if(!$(this).parents('.log').hasClass('ex')){
               $(this).parents('.log').addClass('ex');
           }
//            $(this).();
       });
       $('.nn').click(function(){
            // next
            l = parseInt($(this).parents('.log').attr('indx')) + 1;
            let extra = Math.abs($(this).offset().top - $(window).scrollTop());
            let t = $(`[indx='`+l+`']`).offset().top + 3;
            a(t - extra);
       })
       $('.np').click(function(){
            l = parseInt($(this).parents('.log').attr('indx')) - 1;
            let extra = Math.abs($(this).offset().top - $(window).scrollTop());
            let lines = $(`[indx='`+l+`']`);
            if(lines.length){
                a(lines.offset().top + 3 - extra);
            }else{
                a(0);
            }
       });
       $('.c').click(function(a){
           if($('.log').hasClass('ex'))
                $('.log').removeClass('ex')
           else
               $('.log').addClass('ex');
       })
//       $('.log-c').slideUp();

       function a(a, animate){
            animate = 150;
            if(animate){
                $('html,body').animate({scrollTop: a}, animate);
            }else{
                $('html,body').scrollTop(a);
            }
       }
  });
</script>
<body>
    <div class='top-right'>
    <span class='fn'>
    <button type='button' class='c'>collapse</button>
</span>
        <select name='file' class='f'>
        $selectHtml
        </select>
    </div>
    <div class='logs'>
       $logsHtml
    </div>
</body>
</html>
        ";
    }
}

$log = new LogsViewer();
echo $log->render();
